function initCharts() {
    let script = document.createElement("script");
    script.type = 'text/javascript';
    script.src = "_content/ChartJs.Blazor/Chart.bundle.min.js"
    document.getElementsByTagName('body')[0].appendChild(script);
}
initCharts();

export function createChart(elementId,axisCategories,dataSets,options,type) {
    let element = document.getElementById(elementId);   
    (function () {
        new Chart(element, {
            "type": type,
            "data": {
                "labels": axisCategories,
                "datasets":dataSets
            },
            options:options
        });
    });
}
